mapper.select:
		
		WHERE
		ps.bas_addr REGEXP #{loc}
        ORDER BY ps.rgdt DESC
		limit #{rowNo},#{size}

		
		WHERE
		ps.stat = 'Y'
		AND
		ps.titl Like #{word}
		OR
		pe.nick Like #{word}
		OR
		pe.name Like #{word}
        ORDER BY ps.rgdt DESC
		limit #{rowNo},#{size}

		
		WHERE
		ps.stat = 'Y'
		AND
		btag.btno=#{btno}
        ORDER BY ps.rgdt DESC
		limit #{rowNo},#{size}

mapper.update:
update p_cls
titl = #{titl}, conts = #{conts}, pric = #{pric}, rgdt = now(), time = #{time}, capa = #{capa}, cfile = #{cfile}, tinfo = #{tinfo}, cinfo = #{cinfo}, pstno = #{pstno}, bas_addr = #{basAddr}, det_addr = #{detAddr}, edt = now(), mono = #{mono}, mtno = #{mtno} star = #{star}
where cno = #{no}

update p_cls

	note = #{note},
	stat = #{stat}

where
cno = #{no}
